const orderCards = document.querySelectorAll('.MuiCard-root');
const orders = Array.from(orderCards).map(card => {
  const text = card.innerText;
  const orderNumberMatch = text.match(/ORDER #\s*([\d-]+)/);
  const totalMatch = text.match(/TOTAL[\s\n]*\$([\d,.]+)/i);
  const dateMatch = text.match(/ORDER PLACED[\s\n]*([\d\/]+)/i);
  const statusMatch = text.match(/Arriving by ([^\n]+)|Delivered on ([^\n]+)|Cancelled/i);
  let status = null;
  if (statusMatch) status = statusMatch[1] || statusMatch[2] || 'Cancelled';
  const productEls = card.querySelectorAll('img[alt="Product"]');
  const items = Array.from(productEls).map(img => {
    const container = img.closest('.MuiBox-root')?.parentElement;
    const nameEl = container?.querySelector('p.MuiTypography-body1');
    const priceEl = Array.from(container?.querySelectorAll('p.MuiTypography-body1') || []).find(p => p.textContent.includes('$'));
    return { name: nameEl?.textContent.trim() || null, price: priceEl?.textContent.match(/\$[\d,.]+/)?.[0] || null };
  });
  return { order_number: orderNumberMatch?.[1] || null, total: totalMatch ? '$' + totalMatch[1] : null, date: dateMatch?.[1] || null, status, items };
});
return {items: orders};

const orderCards = Array.from(document.querySelectorAll('.MuiCard-root'));
let targetCard = null;
if ('order_index' in inputs && typeof inputs.order_index === 'number') {
  targetCard = orderCards[inputs.order_index];
} else if ('order_number' in inputs) {
  targetCard = orderCards.find(card => card.textContent.includes(inputs.order_number));
}
if (targetCard) {
  const cancelBtn = Array.from(targetCard.querySelectorAll('button')).find(btn => btn.textContent.trim() === 'Cancel order');
  if (cancelBtn) { cancelBtn.click(); return {success: true}; }
}
return {success: false};

const reviewBtns = Array.from(document.querySelectorAll('button')).filter(btn => btn.textContent.includes('Write a product review'));
const products = reviewBtns.map(btn => {
  const container = btn.closest('.MuiBox-root')?.parentElement;
  const nameEl = container?.querySelector('p.MuiTypography-body1');
  return { name: nameEl?.textContent.trim() || '', button: btn };
});
let targetIdx = typeof inputs.product_index === 'number' ? inputs.product_index : null;
if (targetIdx === null && typeof inputs.product_name === 'string') {
  targetIdx = products.findIndex(p => p.name && p.name.toLowerCase().includes(inputs.product_name.toLowerCase()));
}
if (targetIdx === null) targetIdx = 0;
if (targetIdx >= 0 && products[targetIdx] && products[targetIdx].button) {
  products[targetIdx].button.click();
  return { success: true, selected_product: products[targetIdx].name };
}
return { success: false, error: 'Product not found or no review button.' };

const query = inputs.query;
const searchInputs = document.querySelectorAll('input.MuiInputBase-input.MuiOutlinedInput-input');
const searchButtons = document.querySelectorAll('button[aria-label="Search"]');
let input, button;
for (let i = 0; i < searchInputs.length; i++) {
  if (searchInputs[i].offsetParent !== null) {
    input = searchInputs[i];
    button = searchButtons[i];
    break;
  }
}
if (input && button) {
  const inputSetter = Object.getOwnPropertyDescriptor(window.HTMLInputElement.prototype, 'value').set;
  inputSetter.call(input, query);
  input.dispatchEvent(new Event('input', { bubbles: true }));
  button.click();
  return {success: true};
}
return {success: false, error: 'No visible search bar found'};

const cards = Array.from(document.querySelectorAll('h2[role="link"]')).map(h2 => {
  const container = h2.closest('.MuiGrid2-root');
  const name = h2.textContent.trim();
  let price = null;
  let brand = null;
  if (container) {
    const priceContainer = container.querySelector('[aria-label*="View price details"]');
    if (priceContainer) {
      const priceParts = Array.from(priceContainer.querySelectorAll('p')).map(p => p.textContent.trim());
      if (priceParts.length === 3) { price = priceParts[0] + priceParts[1] + '.' + priceParts[2]; } else { price = priceParts.join(''); }
    }
    const brandEl = container.querySelector('p.MuiTypography-body1');
    if (brandEl && !brandEl.textContent.includes('$')) brand = brandEl.textContent.trim();
  }
  return {name, price, brand};
});
return {items: cards};

const nameEl = document.querySelector('#product-details-header');
const name = nameEl?.textContent.trim() || '';
const priceContainer = document.querySelector('[aria-label="product price"]');
let price = '';
if (priceContainer) { const parts = Array.from(priceContainer.querySelectorAll('p')).map(p => p.textContent.trim()); if (parts.length === 3) { price = parts[0] + parts[1] + '.' + parts[2]; } else { price = parts.join(''); } }
const colorEl = Array.from(document.querySelectorAll('p')).find(p => p.textContent.startsWith('Color:'));
const color = colorEl?.querySelector('span')?.textContent.trim() || '';
const descEl = Array.from(document.querySelectorAll('p')).find(p => p.textContent.includes('About this item'));
const descList = descEl?.parentElement?.querySelector('ul');
let description = descList ? Array.from(descList.querySelectorAll('li')).map(li => li.textContent.trim()).join(' ') : '';
let max_quantity = 1;
const qtySelect = document.querySelector('#quantity-select');
if (qtySelect) {
  qtySelect.dispatchEvent(new MouseEvent('mousedown', {bubbles:true}));
  await new Promise(r=>setTimeout(r,300));
  const options = document.querySelectorAll('li[role="option"]');
  if (options.length > 0) {
    const nums = Array.from(options).map(o => parseInt(o.textContent.match(/\d+/)?.[0] || '0', 10)).filter(n => n > 0);
    max_quantity = Math.max(...nums, 1);
  }
  document.body.click();
  await new Promise(r=>setTimeout(r,100));
}
const ratingContainer = document.querySelector('[aria-labelledby="product-rating"]');
const rating = ratingContainer?.querySelector('p')?.textContent.trim() || null;
const attributes = {};
const attrBoxes = document.querySelectorAll('.MuiBox-root.css-k008qs');
attrBoxes.forEach(box => { const ps = box.querySelectorAll('p'); if (ps.length >= 2) { const key = ps[0].textContent.trim(); const value = ps[1].textContent.trim(); if (key && value) attributes[key] = value; } });
const rows = document.querySelectorAll('tr');
rows.forEach(row => { const cells = row.querySelectorAll('td'); if (cells.length >= 2) { const key = cells[0].textContent.trim(); const value = cells[1].textContent.trim(); if (key && value) attributes[key] = value; } });
const attrStr = Object.entries(attributes).map(([k,v]) => k + ': ' + v).join('. ');
if (attrStr) description = description + ' ' + attrStr;
return {name, price, color, description, max_quantity, rating, attributes};

const itemName = document.querySelector('div[role="combobox"]')?.parentElement?.textContent.match(/^[^\d]+/); const itemPrice = document.querySelector('div[role="combobox"]')?.parentElement?.textContent.match(/\d+\.\d{2}/); const quantityEl = document.querySelector('div[role="combobox"]'); let quantity = 1; if (quantityEl) { const qtyMatch = quantityEl.textContent.match(/\d+/); if (qtyMatch) quantity = parseInt(qtyMatch[0], 10); } const items = [{ name: itemName ? itemName[0].trim() : '', price: itemPrice ? parseFloat(itemPrice[0]) : null, quantity }];
const deliveryOptions = Array.from(document.querySelectorAll('label[aria-label$="delivery option"], label[aria-label$="Key delivery option"], label[aria-label*="Omnizon Day"]')).map((label, idx) => {
  const dateText = label.parentElement.querySelector('p')?.textContent || '';
  const descs = Array.from(label.parentElement.querySelectorAll('p')).map(p=>p.textContent);
  let date = descs.length>0 ? descs[0] : '';
  let labelText = descs.length>1 ? descs[1] : '';
  let priceMatch = label.getAttribute('aria-label').match(/\d+\.\d{2}/);
  let price = priceMatch ? parseFloat(priceMatch[0]) : (labelText.includes('Free') ? 0 : null);
  return { option_index: idx, date, label: labelText, price };
});
const totalMatch = document.body.textContent.match(/Order Total:\s*([\d,.]+)/i);
const total = totalMatch ? parseFloat(totalMatch[1].replace(/,/g,'')) : null;
return { items, delivery_options: deliveryOptions, total };

const labels = Array.from(document.querySelectorAll('label[aria-label*="delivery option"]'));
let target;
if (typeof inputs.option_index === 'number') {
  let idx = inputs.option_index;
  if (idx < 0) idx = labels.length + idx;
  target = labels[idx];
} else if (inputs.date_text) {
  target = labels.find(label => label.textContent.includes(inputs.date_text));
}
if (target) {
  target.click();
}
return {success: !!target};
